/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */


const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1 {...{
      "id": "laravel-54"
    }}>{`Laravel 5.4`}</h1>
    <h2 {...{
      "id": "laravel-installation-and-composer"
    }}>{`Laravel Installation and Composer`}</h2>
    <p>{`Laravel is a PHP framework - basic.`}</p>
    <p>{`Laravel also requires composer.`}</p>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`Commands`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Description`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`composer global require "laravel/installer"`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Global install command laravel`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`laravel new blog`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Create a new blog`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`composer create-project --prefer-dist laravel/laravel blog`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Create Blog with Composer Create-Project`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}><inlineCode parentName="td">{`php artisan serve`}</inlineCode></td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Serve the project locally`}</td>
        </tr>
      </tbody>
    </table>
    <p>{`If you are adding the `}<inlineCode parentName="p">{`laravel`}</inlineCode>{` command globally, then ensure `}<inlineCode parentName="p">{`$HOME/.composer/vendor/bin:$PATH`}</inlineCode>{` is in your path.`}</p>
    <h2 {...{
      "id": "basic-routing-and-views"
    }}>{`Basic Routing and Views`}</h2>
    <p>{`There will be a whole bunch of files etc after generating a Laravel program.`}</p>
    <p>{`Likely we can ignore the middleware once we're in the situation that requires it.`}</p>
    <p>{`The `}<inlineCode parentName="p">{`routes`}</inlineCode>{` is where you will find the routes.`}</p>
    <p>{`This will do a standard `}<inlineCode parentName="p">{`Routes::get('/', function () {})`}</inlineCode>{` for the `}<inlineCode parentName="p">{`blade`}</inlineCode>{` template engine.`}</p>
    <p><strong parentName="p">{`Defining`}</strong></p>
    <pre><code parentName="pre" {...{
        "className": "language-php"
      }}>{`Route::get('/welcome', function () {
    return view('welcome')
});
`}</code></pre>
    <h2 {...{
      "id": "laravel-valet"
    }}>{`Laravel Valet`}</h2>
    <p>{`In the `}<inlineCode parentName="p">{`Dev Environments`}</inlineCode>{`, you can see `}<inlineCode parentName="p">{`Homestead`}</inlineCode>{` and `}<inlineCode parentName="p">{`Valet`}</inlineCode>{` - `}<inlineCode parentName="p">{`Homestead`}</inlineCode>{` is a preconfigured Vagrant box which uses VMs.`}</p>
    <p><inlineCode parentName="p">{`Valet`}</inlineCode>{` is specifically for the Mac. It is a Laravel Dev Environment. No need to set up or alter any files.`}</p>
    <p>{`If you go `}<inlineCode parentName="p">{`laravel new app`}</inlineCode>{` you could straight away hit up `}<inlineCode parentName="p">{`app.dev`}</inlineCode>{`.`}</p>
    <p>{`This requires PHP 7.1. You can install `}<inlineCode parentName="p">{`Valet`}</inlineCode>{` with composer as a global requirement. Run `}<inlineCode parentName="p">{`valet install`}</inlineCode>{`.`}</p>
    <p><inlineCode parentName="p">{`brew services start mysql`}</inlineCode>{` will start `}<inlineCode parentName="p">{`mysql`}</inlineCode>{` if it was installed via Brew and you're wishing to use.`}</p>
    <p><inlineCode parentName="p">{`valet park`}</inlineCode>{` is used to park the root of the directory that you wish to use for finding projects.`}</p>
    <p><inlineCode parentName="p">{`valet secure`}</inlineCode>{` will even secure the website.`}</p>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
