  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"segmentio"}}>{`SegmentIO`}</MDXTag>
<MDXTag name="p" components={components}>{`Segment IO abstracts the layers of integration from code base to external GUI UI.`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"links"}}>{`Links`}</MDXTag>
<MDXTag name="p" components={components}>{`Reference can be `}<MDXTag name="a" components={components} parentName="p" props={{"href":"https://segment.com/"}}>{`found here on the main site`}</MDXTag>{`.`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"prerequisites"}}>{`Prerequisites`}</MDXTag>
<MDXTag name="ol" components={components}>
<MDXTag name="li" components={components} parentName="ol">{`Install the Segment `}<MDXTag name="inlineCode" components={components} parentName="li">{`js`}</MDXTag>{` module using Kratos.`}</MDXTag>
<MDXTag name="li" components={components} parentName="ol">{`Add REACT_APP_SEGMENT_KEY to the .env file with the correct key.`}</MDXTag>
</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"intergration"}}>{`Intergration`}</MDXTag>
<MDXTag name="p" components={components}>{`The `}<MDXTag name="inlineCode" components={components} parentName="p">{`init()`}</MDXTag>{` function runs a basic command instantiate Segment.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`export default class Segment {
 static init() {
  var analytics = (window.analytics = window.analytics || []);
  if (!analytics.initialize) {
   if (analytics.invoked) {
    window.console &&
     console.error &&
     console.error('Segment snippet included twice.');
   } else {
    analytics.invoked = !0;
    analytics.methods = [
     'trackSubmit',
     'trackClick',
     'trackLink',
     'trackForm',
     'pageview',
     'identify',
     'reset',
     'group',
     'track',
     'ready',
     'alias',
     'debug',
     'page',
     'once',
     'off',
     'on'
    ];
    analytics.factory = function(t) {
     return function() {
      var e = Array.prototype.slice.call(arguments);
      e.unshift(t);
      analytics.push(e);
      return analytics;
     };
    };
    for (var t = 0; t < analytics.methods.length; t++) {
     var e = analytics.methods[t];
     analytics[e] = analytics.factory(e);
    }
    analytics.load = function(t, e) {
     var n = document.createElement('script');
     n.type = 'text/javascript';
     n.async = !0;
     n.src =
      'https://cdn.segment.com/analytics.js/v1/' + t + '/analytics.min.js';
     var a = document.getElementsByTagName('script')[0];
     a.parentNode.insertBefore(n, a);
     analytics._loadOptions = e;
    };
    analytics.SNIPPET_VERSION = '4.1.0';
    analytics.load(process.env.REACT_APP_SEGMENT_KEY);
    analytics.page();
   }
  }
 }
}
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"usage"}}>{`Usage`}</MDXTag>
<MDXTag name="p" components={components}>{`The recommendation would be to use the `}<MDXTag name="inlineCode" components={components} parentName="p">{`js`}</MDXTag>{` module `}<MDXTag name="inlineCode" components={components} parentName="p">{`Emitter`}</MDXTag>{` to abstract all the logic.`}</MDXTag>
<MDXTag name="p" components={components}>{`Using throughout the app can happen through the `}<MDXTag name="inlineCode" components={components} parentName="p">{`window`}</MDXTag>{` object.`}</MDXTag>
<MDXTag name="p" components={components}>{`Example:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`window.analytics.track('Email Opened', data);
`}</MDXTag></MDXTag>
           </MDXTag>
  }
}
