  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"sinon-js"}}>{`Sinon JS`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"table-of-contents"}}>{`Table of contents`}</MDXTag>
{/* TOC */}
<MDXTag name="ul" components={components}>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"#sinon-js"}}>{`Sinon JS`}</MDXTag><MDXTag name="ul" components={components} parentName="li">
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"#table-of-contents"}}>{`Table of contents`}</MDXTag></MDXTag>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"#basic-example"}}>{`Basic example`}</MDXTag></MDXTag>
</MDXTag></MDXTag>
</MDXTag>
{/* /TOC */}
<MDXTag name="h2" components={components} props={{"id":"basic-example"}}>{`Basic example`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`// form/index.js
import { create } from 'apisauce';

export const api =
    process.env.NODE_ENV === 'production'
        ? create({
              baseURL: 'http://',
              headers: {
                  Accept: 'application/vnd.github.v3+json'
              }
          })
        : create({
              baseURL: 'http://localhost:4444',
              headers: {
                  'Content-Type': 'application/json'
              }
          });

export default class Form {
    static submit = async (values) => {
        // ! This is what I am targeting
        const res = await api.post('/form', values);
        return res;
    };
}

// form/form.mocha.js
import form, { api } from './index';
import sinon from 'sinon';

describe('#submit()', function() {
    it('should return 201', (done) => {
        (async () => {
            // ! We'll stub api.post so a request is not sent
            var post = sinon.stub(api, 'post');
            post.onCall(0).returns({ ok: true, status: 201 });

            const res = await form.submit({ test: true });
            post.restore();
            expect(res.status).to.equal(201);
            done();
        })();
    });
});
`}</MDXTag></MDXTag>
           </MDXTag>
  }
}
