/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */


const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1 {...{
      "id": "design-patterns"
    }}>{`Design Patterns`}</h1>
    <p>{`When it comes to the base GOF Object-Orientated patterns, the design patterns breaks themselves down into three smaller `}<inlineCode parentName="p">{`purposes`}</inlineCode>{` (creational, structural, behavioural) and within those purposes can be further broken down into `}<inlineCode parentName="p">{`scope`}</inlineCode>{` (class, object).`}</p>
    <h2 {...{
      "id": "purpose-breakdown"
    }}>{`Purpose Breakdown`}</h2>
    <table>
      <thead parentName="table">
        <tr parentName="thead">
          <th parentName="tr" {...{
            "align": null
          }}>{`Purpose`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Class`}</th>
          <th parentName="tr" {...{
            "align": null
          }}>{`Object`}</th>
        </tr>
      </thead>
      <tbody parentName="table">
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`Creational`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Factory Method`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Abstract Factory, Builder, Prototype, Singleton`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`Structural`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Adapter (class)`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Adapter (object), Bridge, Composite, Decorator, Facade, Flyweight, Proxy`}</td>
        </tr>
        <tr parentName="tbody">
          <td parentName="tr" {...{
            "align": null
          }}>{`Behavioural`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Interpreter, Template Method`}</td>
          <td parentName="tr" {...{
            "align": null
          }}>{`Chain of Responsibility, Command, Iterator, Mediator, Memento, Observer, State, Strategy, Visitor`}</td>
        </tr>
      </tbody>
    </table>
    <h2 {...{
      "id": "creational-patterns"
    }}>{`Creational Patterns`}</h2>
    <p>{`Creational design patterns abstract the instantiation process. They help make a system independent of how its object are created, composed and represented. A class creational pattern will delegate instantiation to another object.`}</p>
    <p>{`These patterns particularly help when systems, adhering to the principle of prefering composition over inheritance, become more complex.`}</p>
    <p>{`There are two recurring themes with these patterns:`}</p>
    <ol>
      <li parentName="ol">{`They encapsulate all knowledge about which concrete classes the system uses.`}</li>
      <li parentName="ol">{`They hide how instances of these classes are created and put together.`}</li>
    </ol>
    <p>{`(TODO: finish notes on other patterns)`}</p>
    <h2 {...{
      "id": "resources"
    }}>{`Resources`}</h2>
    <ul>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://en.wikipedia.org/wiki/Software_design_pattern#Classification_and_list"
        }}>{`A good resource for all patterns`}</a></li>
    </ul>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
