/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */


const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1 {...{
      "id": "css-regression-testing"
    }}>{`CSS Regression Testing`}</h1>
    <p>{`We can achieve CSS regression testing by using an assertion library in conjuction with Puppeteer and Pixel Diff.`}</p>
    <h2 {...{
      "id": "prequisites"
    }}>{`Prequisites`}</h2>
    <h3 {...{
      "id": "using-yarn"
    }}>{`Using yarn`}</h3>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`yarn install --dev mocha chai puppeteer pixelmatch pngfs babel-polyfill babel-preset-env babel-plugin-module-resolver
`}</code></pre>
    <h3 {...{
      "id": "using-kratos"
    }}>{`Using kratos`}</h3>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`kratos install js-controllers pixeldiff
kratos install js-test regression
shotgun run
`}</code></pre>
    <h3 {...{
      "id": "folder-setup"
    }}>{`Folder setup`}</h3>
    <p>{`Ensure that you have a `}<inlineCode parentName="p">{`regression`}</inlineCode>{` folder to store all the images.`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`# Example regression layout
regression
├── diff
│   └── test.png
├── src
│   └── test.png
└── temp
    └── test.png

3 directories, 3 files
`}</code></pre>
    <h2 {...{
      "id": "using-regression-cli"
    }}>{`Using Regression CLI`}</h2>
    <h3 {...{
      "id": "preparation"
    }}>{`Preparation`}</h3>
    <ol>
      <li parentName="ol">{`Set the imports for the router file.`}</li>
      <li parentName="ol">{`Set the dev router.`}</li>
      <li parentName="ol">{`Set the array in the .puppeteer file.`}</li>
      <li parentName="ol">{`Ensure the devRouter is being used.`}</li>
      <li parentName="ol">{`Ensure app is running.`}</li>
      <li parentName="ol">{`Run source to set the base image.`}</li>
      <li parentName="ol">{`Run compare to compare source to current.`}</li>
    </ol>
    <h2 {...{
      "id": "application"
    }}>{`Application`}</h2>
    <h3 {...{
      "id": "1-create-a-babelrc-file"
    }}>{`1. Create a babelrc file`}</h3>
    <pre><code parentName="pre" {...{
        "className": "language-json"
      }}>{`{
  "presets": ["env"],
  "plugins": [
    [
      "module-resolver",
      {
        "alias": {
          "controllers": "./controllers"
        }
      }
    ]
  ]
}
`}</code></pre>
    <h3 {...{
      "id": "2-saving-a-screenshot"
    }}>{`2. Saving a screenshot`}</h3>
    <p>{`This is not the test itself but a simple node script to get the initial base working:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`const PixelDiff = require('./controllers/pixeldiff');
const puppeteer = require('puppeteer');

(async () => {
  const browser = await puppeteer.launch();
  const page = await browser.newPage();

  console.log('Opening browser');
  await page.goto('http://localhost:3000');

  const el = await page.$('.homeSplashFade');
  await el.screenshot({ path: 'regression/src/test.png' });

  console.log('Closing browser');
  await browser.close();

  /*
    console.log('Comparing images');
    const res = await PixelDiff.diff({imgOnePath: 'test.png', imgTwoPath: 'test2.png', dest: 'dest.png', output: true});
    console.log(res);
    */
})();
`}</code></pre>
    <h3 {...{
      "id": "3-writing-the-test-file"
    }}>{`3. Writing the test file`}</h3>
    <p>{`Create your test file. Any example test file looks like the following:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`/**
 * Regression tests
 * @author Dennis O'Keeffe
 */
require('babel-polyfill');
const expect = require('chai').expect;
const cwd = process.cwd();
const PixelDiff = require('controllers/pixeldiff');
const puppeteer = require('puppeteer');

console.log(PixelDiff);
console.log(cwd);

const screenshot = async (selector, savePath, location = '/') => {
  const browser = await puppeteer.launch();
  const page = await browser.newPage();

  console.log('Opening browser');
  await page.goto('http://localhost:3000');

  const el = await page.$('#root');
  await el.screenshot({ path: savePath });

  console.log('Closing browser');
  await browser.close();
};

describe('It works functionality', () => {
  it('Expects true to be true', () => {
    expect(true).to.be.true;
  });
});

describe('Image regression testing', () => {
  it('has no pixel difference', async () => {
    console.log('Comparing images');
    await screenshot('.homeSplashFade', cwd + '/regression/temp/test.png');

    const res = await PixelDiff.diff({
      imgOnePath: cwd + '/regression/src/test.png',
      imgTwoPath: cwd + '/regression/temp/test.png',
      dest: cwd + '/regression/diff/test.png',
      output: true
    });
    expect(res).to.equal(0);
  });
});
`}</code></pre>
    <h3 {...{
      "id": "4-running-the-test-from-the-cli"
    }}>{`4. Running the test from the CLI`}</h3>
    <pre><code parentName="pre" {...{
        "className": "language-bash"
      }}>{`mocha --compilers js:babel-core/register --timeout 0 ./test/regression/regression.mocha.js
`}</code></pre>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
