/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */


const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1 {...{
      "id": "accessibility-course"
    }}>{`Accessibility Course`}</h1>
    <h2 {...{
      "id": "introduction"
    }}>{`Introduction`}</h2>
    <ul>
      <li parentName="ul">{`A lot of times, static sites are more accesible`}</li>
      <li parentName="ul">{`Microsoft Inclusive Toolkit (a set of personas to work with)`}</li>
    </ul>
    <h2 {...{
      "id": "course-coverage"
    }}>{`Course Coverage`}</h2>
    <ul>
      <li parentName="ul">{`Focus management`}</li>
      <li parentName="ul">{`Accouncements`}</li>
      <li parentName="ul">{`Semantic HTML`}</li>
      <li parentName="ul">{`Unobtrusive motion`}</li>
      <li parentName="ul">{`Progressive enhancements`}</li>
      <li parentName="ul">{`Testing`}</li>
    </ul>
    <h2 {...{
      "id": "course-materials"
    }}>{`Course Materials`}</h2>
    <p>{`Found on `}<a parentName="p" {...{
        "href": "https://www.github.com/marcysutton/js-a11y-workshop"
      }}>{`Marcy's Github repo for the course`}</a></p>
    <pre><code parentName="pre" {...{
        "className": "language-shell"
      }}>{`git clone https://www.github.com/marcysutton/js-a11y-workshop
cd js-a11y-workshop
npm install
npx gatsby develop
`}</code></pre>
    <h2 {...{
      "id": "debugging"
    }}>{`Debugging`}</h2>
    <ul>
      <li parentName="ul">{`Render in web browsers`}</li>
      <li parentName="ul">{`Test with keyboard`}</li>
      <li parentName="ul">{`Use a11y web extensions (good for smoke testing)`}</li>
      <li parentName="ul">{`Check color contrast (#1 problem on the web)`}</li>
      <li parentName="ul">{`Test with screen readers (recommended to pair with someone who uses them daily)`}</li>
      <li parentName="ul">{`Use magnification & zoom`}</li>
    </ul>
    <h2 {...{
      "id": "app-testing-in-the-wild"
    }}>{`App Testing In The Wild`}</h2>
    <ul>
      <li parentName="ul">{`If you have trouble figuring out where you are on the screen, click to set focus point`}</li>
      <li parentName="ul">{`Number of items on Slack JSON viewer that couldn't be reached`}</li>
      <li parentName="ul">{`Visual outlines are very important for a number of people`}</li>
      <li parentName="ul"><inlineCode parentName="li">{`<a>`}</inlineCode>{` without `}<inlineCode parentName="li">{`href`}</inlineCode>{` attribute is NOT focusable - if you need something interactive, the button may have been the better choice.`}</li>
      <li parentName="ul">{`Github uses the `}<inlineCode parentName="li">{`Skip to content`}</inlineCode></li>
      <li parentName="ul">{`Elements should not have a tabindex > 0 (best practise more than outright violation)`}</li>
      <li parentName="ul">{`Fn + Cmd + F5 to start Mac voiceover (NOTE: key navigation changes with voiceover)`}</li>
      <li parentName="ul">{`Tests NVDA on Mac using VM`}</li>
      <li parentName="ul">{`Local build is done for testing a11y`}</li>
    </ul>
    <h2 {...{
      "id": "hidden-vs-visible-css"
    }}>{`Hidden vs Visible CSS`}</h2>
    <ul>
      <li parentName="ul">{`Can affect where focus can go`}</li>
      <li parentName="ul">{`Presenter uses `}<inlineCode parentName="li">{`.visually-hidden`}</inlineCode>{` class to take an HTML and render it so it is part of the a11y info but visually hidden visually. Common utility, but sometimes not the right tool.`}</li>
      <li parentName="ul">{`Contents is still there for a11y APIs`}</li>
      <li parentName="ul"><inlineCode parentName="li">{`opacity`}</inlineCode>{` is used for `}<inlineCode parentName="li">{`reserving space`}</inlineCode>{`, still being rendered and contents still available for a11y APIs`}</li>
      <li parentName="ul"><inlineCode parentName="li">{`display: none`}</inlineCode>{` hides from assistive technology (a11y info being ignored)`}</li>
      <li parentName="ul"><inlineCode parentName="li">{`visibility: hidden`}</inlineCode>{` still reserves space, but hides a11y info`}</li>
    </ul>
    <p>{`The visually hidden class used:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-scss"
      }}>{`.visually-hidden {
  border: 0;
  clip: rect(0, 0, 0, 0);
  height: 1px;
  margin: -1px;
  overflow: hidden;
  padding: 0;
  position: absolute;
  width: 1px;
}
`}</code></pre>
    <h2 {...{
      "id": "a11y-tree"
    }}>{`A11y Tree`}</h2>
    <ul>
      <li parentName="ul">{`Created with a11y APIs so that screenreaders have streamlined experience`}</li>
      <li parentName="ul"><inlineCode parentName="li">{`chrome://accessibility`}</inlineCode>{` needs to turn on tree for each tab - very raw version of the a11y tree`}</li>
      <li parentName="ul">{`Great to see how HTML and CSS can impact the a11y tree`}</li>
      <li parentName="ul">{`Hack > use `}<inlineCode parentName="li">{`addEventListener`}</inlineCode>{` for `}<inlineCode parentName="li">{`focusin`}</inlineCode>{` to get `}<inlineCode parentName="li">{`document.activeElement`}</inlineCode></li>
      <li parentName="ul">{`Safari has a weird setting to ensure tab highlights elements`}</li>
    </ul>
    <p>{`The `}<inlineCode parentName="p">{`activeElement`}</inlineCode>{` JavaScript:`}</p>
    <pre><code parentName="pre" {...{
        "className": "language-javascript"
      }}>{`document.body.addEventListener('focusin', (event) => {
  console.log(document.activeElement);
});
`}</code></pre>
    <h2 {...{
      "id": "exercises"
    }}>{`Exercises`}</h2>
    <ol>
      <li parentName="ol">{`Team `}<inlineCode parentName="li">{`No Mouse Mondays`}</inlineCode></li>
      <li parentName="ol">{`Run browser extensions (axe, A11y Insights, Lighthouse, WAVE, NoCoffee)`}</li>
      <li parentName="ol">{`DevTools A11y (chrome color picker & a11y pane, Firefox a11y tools)`}</li>
    </ol>
    <h2 {...{
      "id": "screen-readers"
    }}>{`Screen Readers`}</h2>
    <ul>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://webaim.org/articles/voiceover"
        }}>{`Voiceover`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://webaim.org/articles/nvda"
        }}>{`NVDA`}</a></li>
      <li parentName="ul"><a parentName="li" {...{
          "href": "https://webaim.org/articles/jaws"
        }}>{`JAWS`}</a>{` (most important for those who use screen readers)`}</li>
    </ul>
    <h3 {...{
      "id": "useful-combos"
    }}>{`Useful combos`}</h3>
    <ul>
      <li parentName="ul">{`OSX Voiceover and Safari`}</li>
      <li parentName="ul">{`NVDA and Firefox Windows`}</li>
      <li parentName="ul">{`JAWS and IE11 or Edge`}</li>
      <li parentName="ul">{`iOS Voiceover and Safari`}</li>
      <li parentName="ul">{`Android a11y and Chrome`}</li>
      <li parentName="ul">{`Orca on Linux`}</li>
    </ul>
    <h2 {...{
      "id": "magnification"
    }}>{`Magnification`}</h2>
    <ul>
      <li parentName="ul">{`Browser zoom (500%, 200% for guidelines)`}</li>
      <li parentName="ul">{`OS-level zoom`}</li>
      <li parentName="ul">{`ZoomText & other assistive tech`}</li>
    </ul>
    <p>{`Watch out for:`}</p>
    <ul>
      <li parentName="ul">{`Page scrolling`}</li>
      <li parentName="ul">{`Font sizes & scaling`}</li>
      <li parentName="ul">{`UX of interactions when zoomed`}</li>
    </ul>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
