<MDXTag name="h2" components={components} props={{"id":"rscss-8-element-variants"}}>{`RSCSS-8: Element Variants`}</MDXTag>
<MDXTag name="p" components={components}>{`Elements may also have variants.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-css"}}>{`.shopping-card {
