<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`/*
    Use this for the optimized build
    and serve out with Docker
 */

var fs = require('fs');
var dotenv = require('dotenv').config;

// Main starting point of the application
const express = require('express');
const http = require('http');
const bodyParser = require('body-parser');
const morgan = require('morgan');
const app = express();
const cors = require('cors');
const spawn = require('child_process').spawn;
const path = require('path');
const React = require('react');
const renderToString = require('react-dom/server').renderToString;
const Home = require('./components/home/Home').default;

// App Setup
app.use(morgan('combined'));
app.use(cors());
app.use(bodyParser.json({ type: '*/*' }));

app.use(express.static(path.resolve(__dirname, 'build')));

app.get('/', (req, res) => {
    const content = renderToString(<Home />);

    res.send(content);
});

app.get('*', (req, res) => {
    res.sendFile(path.resolve(__dirname, 'build', 'index.html'));
});

// Server Setup
const port = process.env.PORT || 3000;
const server = http.createServer(app);
server.listen(port);
console.log('Server listening on:', port);
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`Given a Webpack file like the following, we can bundle the .js file and then use a Node server to run and return the code:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`const path = require('path');

module.exports = {
    // Target Nodehs
    target: 'node',
    // Looking for the root of server app
    entry: './src/app.js',
    output: {
        filename: 'server.js',
        path: path.resolve(__dirname, 'build')
    },
    // Run Babel on every file
    module: {
        rules: [
            {
                test: /\.js?\$/,
                loader: 'babel-loader',
                exclude: /node_modules/,
                options: {
                    presets: [
                        'react',
                        'stage-0',
                        ['env', { targets: { browsers: ['last 2 versions']}}]
                    ]
                }
            }
        ]
    }
}
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`If using with `}<MDXTag name="inlineCode" components={components} parentName="p">{`create-react-app`}</MDXTag>{` - add the following config:`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`// config/paths.js

module.exports = {
    ...
    appServer: resolveApp('server'),
    serverIndexJs: resolveApp('src/server.js'),
    ...
