/* @jsx mdx */
  import React from 'react'
  import { mdx } from '@mdx-js/react'
  /* @jsx mdx */


const makeShortcode = name => function MDXDefaultShortcode(props) {
  console.warn("Component " + name + " was not imported, exported, or provided by MDXProvider as global scope")
  return <div {...props}/>
};

const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1 {...{
      "id": "laravel-4-basics"
    }}>{`LARAVEL 4 BASICS`}</h1>
    <h2 {...{
      "id": "getting-started-with-laravel"
    }}>{`Getting Started with Laravel`}</h2>
    <h4 {...{
      "id": "installing-virtualbox-homestead--vagrant"
    }}>{`Installing VirtualBox, Homestead & Vagrant`}</h4>
    <ol>
      <li parentName="ol">{`Download VirtualBox for your OS from their website`}</li>
      <li parentName="ol">{`Download Vagrant for your OS from their website`}</li>
      <li parentName="ol">{`Use a command to install install Homestead`}</li>
    </ol>
    <p><inlineCode parentName="p">{`vagrant box add laravel/homestead`}</inlineCode></p>
    <p>{`In a shell, check that you have SSH.`}</p>
    <pre><code parentName="pre" {...{}}>{`cd ~
ls -la
# check for .ssh
`}</code></pre>
    <p>{`If not, you can generate a key.`}</p>
    <p>{`In terminal:`}</p>
    <pre><code parentName="pre" {...{}}>{`ssh-keygen -t rsa -C "youremail@email.com".
`}</code></pre>
    <p>{`There is also a great guide on GitHub.`}</p>
    <p><a parentName="p" {...{
        "href": "https://help.github.com/articles/generating-an-ssh-key/"
      }}>{`https://help.github.com/articles/generating-an-ssh-key/`}</a></p>
    <p><strong parentName="p">{` Init the Homestead.yaml file `}</strong></p>
    <p><inlineCode parentName="p">{`bash init.sh`}</inlineCode></p>
    <p>{`In ~/Homestead, alter the Homestead.yaml file.`}</p>
    <ol>
      <li parentName="ol">{`Replace "me" with the User`}</li>
      <li parentName="ol">{`Under "folders" where it has the map and to, change the to direction to have .../Sites if you don't want the default and to follow along with TH.`}</li>
    </ol>
    <p>{`Sites we can change to anything. Eg. laravel.dev`}</p>
    <p>{`Ensure sites is okay too and that the Sites/`}<inlineCode parentName="p">{`<here>`}</inlineCode>{`/`}<inlineCode parentName="p">{`<here>`}</inlineCode>{` is laravel-basics/public`}</p>
    <p>{`Run the Vagrant Up command in terminal `}<inlineCode parentName="p">{`vagrant up`}</inlineCode></p>
    <p>{`If there is a host issue, change the Homestead.yaml host.`}</p>
    <p><strong parentName="p">{` Getting into the box `}</strong></p>
    <p>{`Connect via SSH`}</p>
    <p>{`run `}<inlineCode parentName="p">{`vagrant ssh`}</inlineCode></p>
    <h4 {...{
      "id": "install-laravel"
    }}>{`Install Laravel`}</h4>
    <p>{`Ensure composer is installed on your external SSH OS.`}</p>
    <p>{`Run `}<inlineCode parentName="p">{`composer global require "laravel/installer"`}</inlineCode></p>
    <p><strong parentName="p">{` Composer Create-Project `}</strong></p>
    <pre><code parentName="pre" {...{}}>{`composer create-project --prefer-dist laravel/laravel laravel-basics
`}</code></pre>
    <p>{`Alternatively...`}</p>
    <pre><code parentName="pre" {...{}}>{`laravel new laravel-basics
`}</code></pre>
    <p><strong parentName="p">{`Note`}</strong>{` laravel-basics can be swapped for anything.`}</p>
    <p>{`Run pwd and check this against what you have in ~/.homestead/Homestead.yaml`}</p>
    <p>{`Then jump to the site (127.0.0.1:8000) to see if it is running successfully.`}</p>
    <p>{`To get rid of the 127.0.0.1, we can change it to another host.`}</p>
    <p>{`cd /etc from the Homestead file and adjust the hosts file in a text editor to make the website easier to navigate to.`}</p>
    <h4 {...{
      "id": "laravel-folder-structure"
    }}>{`Laravel Folder Structure`}</h4>
    <hr></hr>
    <h2 {...{
      "id": "project-setup"
    }}>{`Project Setup`}</h2>
    <hr></hr>
    <h2 {...{
      "id": "laravel-controllers"
    }}>{`Laravel Controllers`}</h2>
    <hr></hr>
    <h2 {...{
      "id": "laravel-and-databases"
    }}>{`Laravel and Databases`}</h2>
    <hr></hr>
    <h2 {...{
      "id": "blade-and-forms"
    }}>{`Blade and Forms`}</h2>
    <hr></hr>
    <h2 {...{
      "id": "validation--flash-messages"
    }}>{`Validation & Flash Messages`}</h2>
    <hr></hr>
    <h2 {...{
      "id": "continuing-crud"
    }}>{`Continuing CRUD`}</h2>
    <hr></hr>
    <h2 {...{
      "id": "relating-data"
    }}>{`Relating Data`}</h2>
    </MDXLayout>;
}

MDXContent.isMDXComponent = true;
