  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"d3"}}>{`D3`}</MDXTag>
<MDXTag name="p" components={components}>{`This all relates back to "D3 in action". Check back to the book for more info.`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"the-power-of-d3"}}>{`The power of d3`}</MDXTag>
<MDXTag name="p" components={components}>{`The main power of d3 comes through the ability to select document elements
`}<MDXTag name="inlineCode" components={components} parentName="p">{`d3.selectAll("circle.a").style("fill", "red").attr("cx", 100);`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`This takes every circle on our page with the class of "a" and turns it red and moves it so that its center is 100 pixels to the right of the left side of our `}<MDXTag name="inlineCode" components={components} parentName="p">{`<svg>`}</MDXTag>{` canvas.`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"working-with-arrays"}}>{`Working with arrays`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`someNumbers = [17, 82, 9, 500, 40];
someColors = ['blue', 'red', 'chartreuse', 'orange'];
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`One example of a useful array function is .filter(), which returns an array whose elements satisfy a test you provide. For instance, here’s how to create an array out of someNumbers that had values greater than 40:`}</MDXTag>
<MDXTag name="p" components={components}><MDXTag name="inlineCode" components={components} parentName="p">{`someNumbers.filter(function(el) {return el >= 40});`}</MDXTag></MDXTag>
           </MDXTag>
  }
}
