  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"facebook-login"}}>{`Facebook Login`}</MDXTag>
<MDXTag name="p" components={components}>{`If using Expo, first you need to follow the setup instructions.`}</MDXTag>
<MDXTag name="p" components={components}><MDXTag name="a" components={components} parentName="p" props={{"href":"https://docs.expo.io/versions/latest/sdk/facebook"}}>{`https://docs.expo.io/versions/latest/sdk/facebook`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"tldr"}}>{`tl;dr`}</MDXTag>
<MDXTag name="ol" components={components}>
<MDXTag name="li" components={components} parentName="ol">{`Head to `}<MDXTag name="inlineCode" components={components} parentName="li">{`developers.facebook.com`}</MDXTag>{` and set up the login.`}</MDXTag>
<MDXTag name="li" components={components} parentName="ol">{`Update `}<MDXTag name="inlineCode" components={components} parentName="li">{`app.json`}</MDXTag>{` to look like the following:`}</MDXTag>
</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-json"}}>{`{
  "expo": {
    "sdkVersion": "27.0.0",
    "privacy": "unlisted",
    "name": "Exposition",
    "icon": "./assets/icon.png",
    "version": "1.0.0",
    "slug": "nodular-exposition",
    "facebookScheme": "<% fromFacebook %>",
    "facebookAppId": "<% fromFacebook %>",
    "facebookDisplayName": "Exposition",
    "ios": {
      "bundleIdentifier": "com.nodular.exposition"
    },
    "android": {
      "package": "com.nodular.exposition"
    }
  }
}
`}</MDXTag></MDXTag>
<MDXTag name="ol" components={components} props={{"start":3}}>
<MDXTag name="li" components={components} parentName="ol">{`Install the ExpoFacebook common js module.`}</MDXTag>
<MDXTag name="li" components={components} parentName="ol">{`Update the code flow for Amplify to be similar to the one `}<MDXTag name="a" components={components} parentName="li" props={{"href":"https://docs.aws.amazon.com/cognito/latest/developerguide/facebook.html"}}>{`provided from their website`}</MDXTag>{`:`}</MDXTag>
</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`FB.login(function(response) {
  // Check if the user logged in successfully.
  if (response.authResponse) {
    console.log('You are now logged in.');

    // Add the Facebook access token to the Cognito credentials login map.
    AWS.config.credentials = new AWS.CognitoIdentityCredentials({
      IdentityPoolId: 'IDENTITY_POOL_ID',
      Logins: {
        'graph.facebook.com': response.authResponse.accessToken
      }
    });

    // Obtain AWS credentials
    AWS.config.credentials.get(function() {
      // Access AWS resources here.
    });
  } else {
    console.log('There was a problem logging you in.');
  }
});
`}</MDXTag></MDXTag>
<MDXTag name="ol" components={components} props={{"start":5}}>
<MDXTag name="li" components={components} parentName="ol">{`Ensure auth is added to the project `}<MDXTag name="inlineCode" components={components} parentName="li">{`wsmobile user-signin configure`}</MDXTag>{` and selecting `}<MDXTag name="inlineCode" components={components} parentName="li">{`Advanced`}</MDXTag>{` > `}<MDXTag name="inlineCode" components={components} parentName="li">{`Add Facebook`}</MDXTag>{`.`}</MDXTag>
</MDXTag>
           </MDXTag>
  }
}
