  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h2" components={components} props={{"id":"testing"}}>{`Testing`}</MDXTag>
<MDXTag name="p" components={components}>{`The test files are stored in the test folders and require the dev dependencies `}<MDXTag name="inlineCode" components={components} parentName="p">{`sinonjs, jsdom, mocha, chai`}</MDXTag>{`.`}</MDXTag>
<MDXTag name="p" components={components}>{`Run `}<MDXTag name="inlineCode" components={components} parentName="p">{`npm test`}</MDXTag>{` to view the tests files that are written in that folder.`}</MDXTag>
<MDXTag name="p" components={components}>{`Testing is still primitive and should be BDD (behaviour driven). Tests that are too specific will cause issues.`}</MDXTag>
<MDXTag name="p" components={components}><MDXTag name="strong" components={components} parentName="p">{`mocha and chai`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`These two are used in tandem to assert/expect values. Check out the Chai documentation for relative examples.`}</MDXTag>
<MDXTag name="p" components={components}>{`Tests consist of test suites and test specs. In order to recreate DOM elements if you are looking to test values etc. from functionality, you can use the `}<MDXTag name="inlineCode" components={components} parentName="p">{`jsdom`}</MDXTag>{` library and import `}<MDXTag name="inlineCode" components={components} parentName="p">{`jquery`}</MDXTag>{`.`}</MDXTag>
<MDXTag name="p" components={components}>{`Below is a small example of a test set up.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`import jsdom from 'jsdom-global';
import jQuery from '../bower_components/jquery/dist/jquery.min.js';

import ModuleToTest from 'path/to/module'l

// require sinon/chai
const sinon = require('sinon');
const expect = require('chai').expect;

// Test suite
describe('These tests are supposed to test some functionality', () => {

    let \$;
    let ageGateFilters;
    jsdom();

    // this function will run before every Test Spec
    before(function () {
        \$ = jQuery;
        const params = ['param1', 'param2', 'param3']

        // create instance
        const moduleToTest = new ModuleToTest(...params);
    });

    // Test Spec (unit test)
    it('This test should return ok', () => {
        expect(true).to.be.ok;  // returns ok
    });

    // Test Spec (unit test)
    it('This test should return true for the ModuleToTest function', () => {
        expect(moduleToTest.func()).to.equal(true); // returns ok if result is true
    });

});
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}><MDXTag name="strong" components={components} parentName="p">{`sinon.js`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`This library is used to essentially stub data for functions that require things like ajax etc. No tests currently use this effectively. Examples will be added later if used.`}</MDXTag>
<MDXTag name="p" components={components}>{`For now, I will show an example of a `}<MDXTag name="inlineCode" components={components} parentName="p">{`spy`}</MDXTag>{` and a `}<MDXTag name="inlineCode" components={components} parentName="p">{`stub`}</MDXTag>{` (as this may the most relevant): `}</MDXTag>
<MDXTag name="p" components={components}><MDXTag name="em" components={components} parentName="p">{`For the Spy`}</MDXTag></MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`// in module Example

export default class Example {

    callout() {
        let a = 1;
        let b = 2;

        return target(a,b);
    }

}

// in a test file

import Example from 'Example';

// Test suite
describe('A test suite using sinon', () => {

    // this function will run before every Test Spec
    before(function () {
        \$ = jQuery;
        const params = ['param1', 'param2', 'param3']

        // create instance
        const example = new Example(...params);
    });

    // Test spec (unit test)
    it('Should return that the async func was called', () => {

        let targetSpy = sinon.spy(example, 'target');

        // Now, any time we call the function, the spy logs information about it
        example.callout();

        assert(targetSpy.calledOnce); // returns true
    });
});
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}><MDXTag name="em" components={components} parentName="p">{`For the Stub`}</MDXTag></MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`// in module Example

export default class Example {

    callout(param, callback) {
        \$.ajax({
            // whatever it normally is
        }, callback);
    }

}

// in a test file

import Example from 'Example';

// Test suite
describe('A test suite using sinon', () => {

    // this function will run before every Test Spec
    before(function () {
        \$ = jQuery;
        const params = ['param1', 'param2', 'param3']

        // create instance
        const example = new Example(...params);
    });

    // Test spec (unit test)
    it('Should call callback after saving', () => {

        //We'll stub \$.post so a request is not sent
        const post = sinon.stub(\$, 'ajax');
        post.yields();

        //We can use a spy as the callback so it's easy to verify
        var callback = sinon.spy();

        example.callout(param, callback);

        post.restore();
        sinon.assert.calledOnce(callback); // returns true
    });
});
`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}><MDXTag name="strong" components={components} parentName="p">{`jsdom`}</MDXTag></MDXTag>
<MDXTag name="p" components={components}>{`jsdom is a library that allows you to write a `}<MDXTag name="inlineCode" components={components} parentName="p">{`innerHTML`}</MDXTag>{` var for the test to use if you want to test out the jQuery values for a test.`}</MDXTag>
<MDXTag name="p" components={components}>{`For an example, checkout yt `}<MDXTag name="inlineCode" components={components} parentName="p">{`test/agegate_test.js`}</MDXTag>{`.`}</MDXTag>
           </MDXTag>
  }
}
