  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="hr" components={components}></MDXTag>
<MDXTag name="p" components={components}>{`Author: Dennis O'Keeffe`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"section-testing"}}>{`Section: Testing`}</MDXTag>
<MDXTag name="h1" components={components} props={{"id":"puppeteer"}}>{`Puppeteer`}</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"resources"}}>{`Resources`}</MDXTag>
<MDXTag name="ul" components={components}>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"https://mochajs.org/"}}>{`Mocha Website`}</MDXTag></MDXTag>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"http://www.chaijs.com/api/"}}>{`Chai API`}</MDXTag></MDXTag>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"http://airbnb.io/enzyme/docs/api/"}}>{`Enzyme API`}</MDXTag></MDXTag>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"https://github.com/GoogleChrome/puppeteer/blob/master/docs/api.md"}}>{`Puppeteer API`}</MDXTag></MDXTag>
</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"usage"}}>{`Usage`}</MDXTag>
<MDXTag name="p" components={components}>{`To test the UI itself, we can use Puppeteer, Chrome's Headless Browser to evaluate on the UI itself. Puppeteer can also be set to have `}<MDXTag name="inlineCode" components={components} parentName="p">{`headless: false`}</MDXTag>{`, allowing the user to record or watch the test on their screen.`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`const puppeteer = require('puppeteer');

// Test suite
describe('PageHome functionality', () => {
  // Test suite within another test suite - useful for subsectioning tests within a component/page
  describe('Simple Puppeteer UI test for form elements', () => {
    it('firstName and lastName from Puppeteer deep equal expected schema', async () => {
      const expected = {
        firstName: 'Hello',
        lastName: 'World'
      };

      const browser = await puppeteer.launch({ headless: false });
      const page = await browser.newPage();
      await page.goto('http://localhost:3000');
      await page.screenshot({ path: 'example.png' }); // create an example screenshot of current UI state
      await page.type('input[name="firstName"]', 'Hello');
      const firstName = await page.\$eval(
        'input[name="firstName"]',
        (el) => el.value
      );

      await page.waitFor(500);
      await page.click('#next');
      await page.waitForSelector('input[name="lastName"]');
      await page.type('input[name="lastName"]', 'World');
      const lastName = await page.\$eval(
        'input[name="lastName"]',
        (el) => el.value
      );

      await page.waitFor(500);
      const formData = {
        firstName: firstName,
        lastName: lastName
      };

      await browser.close();

      expect(formData).to.deep.equal(expected); // evauates to true if form fields hold correct value
    });
  });
});
`}</MDXTag></MDXTag>
<MDXTag name="h2" components={components} props={{"id":"logging"}}>{`Logging`}</MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{}}>{`// output logs
page.on('console', msg => console.log('PAGE LOG:', msg.text()));

// show devtools
const browser = await puppeteer.launch({devtools: true});
`}</MDXTag></MDXTag>
           </MDXTag>
  }
}
