  import React from 'react'
  import { MDXTag } from '@mdx-js/tag'


export default class MDXContent extends React.Component {
  constructor(props) {
    super(props)
    this.layout = null
  }
  render() {
    const { components, ...props } = this.props

    return <MDXTag
             name="wrapper"

             components={components}><MDXTag name="h1" components={components} props={{"id":"using-stripe-with-node"}}>{`Using Stripe with Node`}</MDXTag>
{/* TOC */}
<MDXTag name="ul" components={components}>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"#using-stripe-with-node"}}>{`Using Stripe with Node`}</MDXTag><MDXTag name="ul" components={components} parentName="li">
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"#prereqs"}}>{`Prereqs`}</MDXTag></MDXTag>
<MDXTag name="li" components={components} parentName="ul"><MDXTag name="a" components={components} parentName="li" props={{"href":"#testing-with-express"}}>{`Testing with Express`}</MDXTag></MDXTag>
</MDXTag></MDXTag>
</MDXTag>
{/* /TOC */}
<MDXTag name="h2" components={components} props={{"id":"prereqs"}}>{`Prereqs`}</MDXTag>
<MDXTag name="ol" components={components}>
<MDXTag name="li" components={components} parentName="ol">{`Sign up`}</MDXTag>
<MDXTag name="li" components={components} parentName="ol"><MDXTag name="inlineCode" components={components} parentName="li">{`yarn add stripe`}</MDXTag>{` to your Node.js Project`}</MDXTag>
<MDXTag name="li" components={components} parentName="ol">{`Build out a server`}</MDXTag>
</MDXTag>
<MDXTag name="h2" components={components} props={{"id":"testing-with-express"}}>{`Testing with Express`}</MDXTag>
<MDXTag name="p" components={components}>{`Configure the .env file and upload`}</MDXTag>
<MDXTag name="p" components={components}><MDXTag name="a" components={components} parentName="p" props={{"href":"https://github.com/stripe/stripe-node"}}>{`Node Stripe Github`}</MDXTag></MDXTag>
<MDXTag name="pre" components={components}><MDXTag name="code" components={components} parentName="pre" props={{"className":"language-javascript"}}>{`const stripe = require('stripe')(process.env.STRIPE_SECRET_KEY);

/* GET home page. */
module.exports = function(app) {
    app.get('/', function(req, res) {
        res.send('Server is healthy');
    });

    app.post('/', function(req, res) {
        console.log(req.body);
        // Create a new customer and then a new charge for that customer:
        stripe.customers
            .create({
                email: 'foo-customer@example.com'
            })
            .then(function(customer) {
                console.log(customer);
                return stripe.customers.createSource(customer.id, {
                    source: {
                        object: 'card',
                        exp_month: 10,
                        exp_year: 2018,
                        number: '4242 4242 4242 4242',
                        cvc: 100
                    }
                });
            })
            .then(function(source) {
                return stripe.charges.create({
                    amount: 1600,
                    currency: 'aud',
                    customer: source.customer
                });
            })
            .then(function(charge) {
                // New charge created on a new customer
                console.log(charge);
            })
            .catch(function(err) {
                // Deal with an error
                console.log(err);
            });

        res.send('Post requests are healthy');
    });
};
`}</MDXTag></MDXTag>
           </MDXTag>
  }
}
